mapper.select:
select A.*
from (select rownum rr, rboard.*
	from (select num, writer, subject, email, content, passwd, reg_date, readcnt, ref, re_step, re_level, attachnm
		from board order by ref desc, re_step asc) rboard) A


	where num = #{num}



		where A.rr between #{start} and #{end}

mapper.update:

update  board set
	 subject 	= #{subject}  	 
	,email 		= #{email} 		
	,content 	= #{content} 	
	,passwd 	= #{passwd} 	
	,attachnm 	= #{attachnm} 
	 where num 	= #{num}
