
public class Star {

	public Star() {
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) {
		
		for(int i=1;i<=5;i++){
			int j=0;
			if (j<i) {
				j+=1;
				for(int k=0; k<j; k++) {
				System.out.println("*");
				}
			}
			}
			
			System.out.println("");
		}
		
	}


